mapper.update:
UPDATE sj_job_summary rt, (
SELECT #{item.successNum} AS success_num, #{item.failNum} AS fail_num, #{item.failReason} AS fail_reason, #{item.stopNum} AS stop_num, #{item.stopReason} AS stop_reason, #{item.cancelNum} AS cancel_num, #{item.cancelReason} AS cancel_reason, #{item.systemTaskType} AS system_task_type, #{item.triggerAt} AS trigger_at, #{item.businessId} AS business_id
) tt SET rt.success_num = tt.success_num, rt.fail_num = tt.fail_num, rt.fail_reason = tt.fail_reason, rt.stop_num = tt.stop_num, rt.stop_reason = tt.stop_reason, rt.cancel_num = tt.cancel_num, rt.cancel_reason = tt.cancel_reason WHERE rt.system_task_type = tt.system_task_type AND rt.business_id = tt.business_id AND rt.trigger_at = tt.trigger_at
