mapper.update:
UPDATE sj_job_summary AS rt SET success_num = tt.success_num, fail_num = tt.fail_num, fail_reason = tt.fail_reason, stop_num = tt.stop_num, stop_reason = tt.stop_reason, cancel_num = tt.cancel_num, cancel_reason = tt.cancel_reason FROM (
SELECT #{item.successNum} AS success_num, #{item.failNum} AS fail_num, #{item.failReason} AS fail_reason, #{item.stopNum} AS stop_num, #{item.stopReason} AS stop_reason, #{item.cancelNum} AS cancel_num, #{item.cancelReason} AS cancel_reason, #{item.systemTaskType} AS system_task_type, #{item.businessId} AS business_id, #{item.triggerAt} AS trigger_at
) AS tt WHERE rt.system_task_type = tt.system_task_type AND rt.business_id = tt.business_id AND rt.trigger_at = tt.trigger_at
