mapper.update:
UPDATE sj_job_summary SET success_num = src.success_num, fail_num = src.fail_num, fail_reason = src.fail_reason, stop_num = src.stop_num, stop_reason = src.stop_reason, cancel_num = src.cancel_num, cancel_reason = src.cancel_reason FROM sj_job_summary AS dest JOIN (
SELECT #{item.successNum} AS success_num, #{item.failNum} AS fail_num, #{item.failReason} AS fail_reason, #{item.stopNum} AS stop_num, #{item.stopReason} AS stop_reason, #{item.cancelNum} AS cancel_num, #{item.cancelReason} AS cancel_reason, #{item.systemTaskType} AS system_task_type, #{item.businessId} AS business_id, #{item.triggerAt} AS trigger_at
) AS src ON (dest.system_task_type = src.system_task_type AND dest.business_id = src.business_id AND dest.trigger_at = src.trigger_at)
